// src/Pages/Summary.js
import React, { useState } from 'react';
import SummaryForm from '../SummaryForm';

const Summary = () => {
  const [summary, setSummary] = useState('');

  const handleGenerateSummary = async (article) => {
    // Send a POST request to your Flask API endpoint
    const response = await fetch('http://your-flask-api/generate_summary', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ summary: article, max_length: 150, min_length: 30, num_sentences: 5 }),
    });

    const data = await response.json();
    setSummary(data.generated_summary);
  };

  return (
    <div>
      <h1>Summary Page</h1>
      <SummaryForm onSubmit={handleGenerateSummary} />
      {summary && (
        <div>
          <h2>Generated Summary:</h2>
          <p>{summary}</p>
        </div>
      )}
    </div>
  );
};

export default Summary;
