// src/Pages/Summary.js
import React, { useState } from 'react';
import SummaryForm from '../SummaryForm';
// Summary Component
const Summary = () => {
  const [summary, setSummary] = useState('');
  const [error, setError] = useState('');

  const handleGenerateSummary = async (article) => {
    try {
      const response = await fetch('http://localhost:5000/generate_summary', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ summary: article, max_length: 150, min_length: 30, num_sentences: 5 }),
      });

      if (!response.ok) {
        throw new Error(`Failed to fetch: ${response.status} - ${response.statusText}`);
      }

      const data = await response.json();
      setSummary(data.generated_summary);
      setError('');
    } catch (error) {
      console.error('Error generating summary:', error.message);
      setError('Failed to generate summary. Please try again.');
      setSummary('');
    }
  };

  return (
    <div>
      <h1>Summary Page</h1>
      <SummaryForm onSubmit={handleGenerateSummary} />
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {summary && (
        <div>
          <h2>Generated Summary:</h2>
          <p>{summary}</p>
        </div>
      )}
    </div>
  );
};


export default Summary;
