// Summary.js

import React, { useState } from 'react';

const Summary = () => {
  const [article, setArticle] = useState('');
  const [numLines, setNumLines] = useState(5); // Default number of lines
  const [generatedSummary, setGeneratedSummary] = useState('');

  const handleGenerateSummary = async () => {
    try {
      const response = await fetch('http://localhost:5001/generate_summary', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          summary: article,
          num_lines: numLines,
        }),
      });

      const data = await response.json();
      setGeneratedSummary(data.generated_summary);
    } catch (error) {
      console.error('Error generating summary:', error);
    }
  };

  return (
    <div>
      <h1>Summary Generator</h1>
      <textarea
        placeholder="Enter your article here..."
        value={article}
        onChange={(e) => setArticle(e.target.value)}
      ></textarea>
      <label>Number of Lines for Summary:</label>
      <input
        type="number"
        value={numLines}
        onChange={(e) => setNumLines(e.target.value)}
      />
      <button onClick={handleGenerateSummary}>Generate Summary</button>
      {generatedSummary && (
        <div>
          <h2>Generated Summary:</h2>
          <div style={{ border: '1px solid #ccc', padding: '10px', marginTop: '10px' }}>
            <p>{generatedSummary}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Summary;
