// src/ArticleForm.js
import React, { useState } from 'react';
import axios from 'axios';

const ArticleForm = ({ onPredictionUpdate }) => {
  const [articleText, setArticleText] = useState('');
  const [prediction, setPrediction] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handlePrediction = async () => {
    try {
      setLoading(true);
      setError(null);

      // Change the endpoint to /predict_movement
      const response = await axios.post('http://localhost:5000/predict_movement', {
        article: { text: articleText },
      });

      const newPrediction = response.data.prediction;
      setPrediction(newPrediction);
      onPredictionUpdate && onPredictionUpdate(newPrediction);
    } catch (error) {
      console.error('Error:', error);
      setError('An error occurred while making the prediction.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <textarea
        placeholder="Enter article text..."
        value={articleText}
        onChange={(e) => setArticleText(e.target.value)}
      />
      <button onClick={handlePrediction} disabled={loading}>
        {loading ? 'Predicting...' : 'Predict'}
      </button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {prediction && <p>Prediction: {prediction}</p>}
    </div>
  );
};

export default ArticleForm;
