// src/ArticleForm.js
import React, { useState } from 'react';
import axios from 'axios';

const ArticleForm = ({ onPredictionUpdate }) => {
  const [articleText, setArticleText] = useState('');
  const [prediction, setPrediction] = useState('');
  const [sentiment, setSentiment] = useState('');
  const [relevanceScores, setRelevanceScores] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handlePrediction = async () => {
    try {
      setLoading(true);
      setError(null);

      const response = await axios.post('http://localhost:5000/predict_movement', {
        article: { text: articleText },
      });

      const newPrediction = response.data.prediction;
      setPrediction(newPrediction);
      onPredictionUpdate && onPredictionUpdate(newPrediction);

      // Fetch sentiment and relevance scores
      const sentimentResponse = await axios.post('http://localhost:5000/predict_sentiment', {
        article: { text: articleText },
      });

      const relevanceResponse = await axios.post('http://localhost:5000/predict_relevance', {
        article: { text: articleText },
      });

      const newSentiment = sentimentResponse.data.sentiment;
      setSentiment(newSentiment);

      const newRelevanceScores = relevanceResponse.data;
      setRelevanceScores(newRelevanceScores);
    } catch (error) {
      console.error('Error:', error);
      setError('An error occurred while making the prediction.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.header}>Stock Prediction App</h1>
      <div style={styles.articleFormContainer}>
        <textarea
          style={styles.articleTextInput}
          placeholder="Enter article text..."
          value={articleText}
          onChange={(e) => setArticleText(e.target.value)}
        />
        <button style={styles.predictButton} onClick={handlePrediction} disabled={loading}>
          {loading ? 'Predicting...' : 'Predict'}
        </button>
        {error && <p style={styles.errorMessage}>{error}</p>}
        {prediction && (
          <div>
            <p style={styles.predictionResult}>Prediction: {prediction}</p>
            <p style={styles.sentimentResult}>Sentiment Score: {sentiment}</p>
            <p style={styles.relevanceResult}>Relevance Scores:</p>
            <ul>
              {Object.entries(relevanceScores).map(([topic, score]) => (
                <li key={topic}>{`${topic}: ${score}`}</li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

const styles = {
  // ... other styles

  sentimentResult: {
    marginTop: '10px',
    fontWeight: 'bold',
    color: '#000', // Set the font color to black
  },
  relevanceResult: {
    marginTop: '10px',
    fontWeight: 'bold',
    color: '#000', // Set the font color to black
  },
};

export default ArticleForm;
